import XCTest
@testable import StubPlay

class StringExtensionsTests: XCTestCase {

    func testFilename() throws {
        XCTAssertEqual(#file.filename, "StringExtensionsTests")
    }
    
    func testDjb2hash() throws {
        XCTAssertEqual("".djb2hash, 5381)
        XCTAssertEqual("".djb2hash, "".djb2hash)
        XCTAssertEqual("hetairas".djb2hash, "hetairas".djb2hash)
        XCTAssertEqual("1234567890987654321234567890987654321".djb2hash, "1234567890987654321234567890987654321".djb2hash)
        XCTAssertEqual("".djb2hash, "".djb2hash)
        
        XCTAssertNotEqual("haggadot".djb2hash, "loathsomenesses".djb2hash)
        XCTAssertNotEqual("hetairas".djb2hash, "mentioner".djb2hash)
    }
}
